import React, { useContext } from 'react';
import { StyleSheet, View, Text,  ScrollView, ActivityIndicator,SafeAreaView, TouchableOpacity, Button, Alert} from 'react-native';
import Constants from 'expo-constants';
import { RootStoreContext } from '../store/RootStoreContext';
import { observer } from 'mobx-react';
console.log("store context", RootStoreContext)

@observer
class CharacterList extends React.Component {
  static contextType=RootStoreContext;
    /* state = {
        people: [],
        loading:true
    }

    async componentDidMount() {
     
        //Have a try and catch block for catching errors.
        try {
            //Assign the promise unresolved first then get the data using the json method. 
            const swApiCall = await fetch('https://swapi.dev/api/people/');
            const swchar = await swApiCall.json();
            this.setState({people: swchar.results, 
                loading: false});
              
        } catch(err) {
            console.log("Error fetching data-----------", err);
        }
    } */
    
    
    componentDidMount () {
        const {characterStore} = this.context;
        characterStore.load();
   }
   
   render() {
       console.log("Context", this.context)
       const {characterStore} = this.context;
         const { people, loading } = characterStore;
         console.log(people)
         const { navigation } = this.props;
         const charList = people.map((item)=> (
             <TouchableOpacity onPress={() => navigation.navigate('CharacterDetails')} key={item.height} >
             <View  style={styles.container}  >
                 <Text style={styles.text} >Name: {item.name}</Text>
                 <Text>Height: {item.height}</Text>
                 <Text>Mass: {item.mass}</Text>
             </View>
             </TouchableOpacity>
            
         ))
       if (loading) {
           return (
           <View>
               <ActivityIndicator size="small" />
              
           </View>)
       }
        return (
             <View style={styles.container}>
               <ScrollView style={styles.scrollView}>
                 <View style={styles.container}>{charList}</View>
               </ScrollView>
            </View> 
        )
       
       /*  const renderCharList=people.map((charItem)=> {
            console.log("helo", charItem.name)
            return ( <View style={styles.container} key={charItem.height}><Text style={styles.text}>{charItem.name}</Text></View>)
        })
       
            if (loading) {
                <ActivityIndicator/>
            }
           return (
                
                 <View style={styles.container}>
               <Text style={styles.text}>{renderCharList}</Text>
               </View>)  */
             
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: Constants.statusBarHeight,
        justifyContent:'space-around'
    },
    text: {
        color: '#fff',
       
       
     }, 
     scrollView: {
        backgroundColor: 'pink',
        
      },
   });
   
export default CharacterList;
