import React from 'react';
import { StyleSheet, View, Text,  ScrollView, FlatList, ActivityIndicator} from 'react-native';
const keyExtractor= ({height}) => height;
class CharacterList extends React.Component {
  
    state = {
        people: [],
        loading:true
    }

    async componentDidMount() {
     
        //Have a try and catch block for catching errors.
        try {
            //Assign the promise unresolved first then get the data using the json method. 
            const swApiCall = await fetch('https://swapi.dev/api/people/');
            const swchar = await swApiCall.json();
            this.setState({people: swchar.results, 
                loading: false});
              
        } catch(err) {
            console.log("Error fetching data-----------", err);
        }
    }
    
    
 
   render() {

         const { people, loading} = this.state;
         const charList = people.map((item)=> (
             <View style={styles.container} key={item.height} >
                 <Text>{item.name}</Text>
             </View>
         ))
       
        return (
           
             <View style={styles.container}>
                {loading && <ActivityIndicator size="small"/>}
                {!loading && (
                   
                        <View style={styles.container}>{charList}</View>
            
                )}
            </View> )
       
       /*  const renderCharList=people.map((charItem)=> {
            console.log("helo", charItem.name)
            return ( <View style={styles.container} key={charItem.height}><Text style={styles.text}>{charItem.name}</Text></View>)
        })
       
            if (loading) {
                <ActivityIndicator/>
            }
           return (
                
                 <View style={styles.container}>
               <Text style={styles.text}>{renderCharList}</Text>
               </View>)  */
             
    }
}

const styles = StyleSheet.create({
    container: {
   flex: 1,
   backgroundColor: '#fff',
   alignItems: 'center',
   justifyContent: 'space-around',
  
    },
    text: {
        color: '#fff'
     }, 
    listItemContainer: {
        borderStyle: 'solid',
        borderColor: '#fff',
        borderBottomWidth: 2,
        flexDirection: 'row',
        justifyContent: 'space-between',
        padding: 20
    },
    charItemHeader: {  
        color: '#fff',
        fontSize: 24,
    },
   });
   
export default CharacterList;
