import {observable, action } from 'mobx';
import {toJS} from 'mobx';
export class FavCharacterStore{

    @observable favChars = []
    @action addToFavList (character, url) {
        console.log("url",  toJS(character))
        
        const addedFavChars = this.favChars.push(character);
        this.favChars.replace(addedFavChars);
        console.log("new array",toJS (favChars));
    }
};
