import React, { useContext } from 'react';
import { StyleSheet, View, Text,  ScrollView, ActivityIndicator,SafeAreaView, TouchableOpacity, Button, Alert} from 'react-native';
import Constants from 'expo-constants';
import { RootStoreContext } from '../store/RootStoreContext';
import { observer } from 'mobx-react';
console.log("store context", RootStoreContext)


@observer
class CharacterList extends React.Component {
    //The contextType property on a class can be assigned a Context object created by React.createContext() 
    //created inside RootSToreContext
    //This lets you consume the nearest current value of that Context type using this.context
    //You can reference this in any of the lifecycle methods including the render function.
  static contextType=RootStoreContext;
    
    componentDidMount () {
        //render data on load()
        const {characterStore} = this.context;
        characterStore.load();
   }
   
   render() {
       console.log("Context", this.context)
       const {characterStore} = this.context;
         const { people, loading } = characterStore;
      
         const { navigation } = this.props;
         const charList = people.map((item)=> (
             <TouchableOpacity onPress={() => this.props.navigation.navigate('CharacterDetails', {name:people.name})} key={item.height} >
             <View  style={styles.container}  >
                 <Text style={styles.text} >Name: {item.name}</Text>
                 <Text>Height: {item.height}</Text>
                 <Text>Mass: {item.mass}</Text>
                 <Button onPress={() => addToFavList()} style={styles.button} title="Add to favs"></Button>
             </View>
             </TouchableOpacity>
            
         ))
       if (loading) {
           return (
           <View>
               <ActivityIndicator size="small" />
              
           </View>)
       }
        return (
             <View style={styles.container}>
               <ScrollView style={styles.scrollView}>
                 <View style={styles.container}>{charList}</View>
               </ScrollView>
            </View> 
        )
       
             
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: Constants.statusBarHeight,
        justifyContent:'space-around'
    },
    text: {
        color: '#fff',
       
       
     }, 
     scrollView: {
        backgroundColor: 'pink',
        
      },
      button: {
        width: 50
      },
   });
   
export default CharacterList;
